FUNCTION printUDTDef
  (
    basename VARCHAR2,
    p        NUMBER,
    s        NUMBER,
    m        NUMBER
  )
  
  RETURN VARCHAR2
IS
  StringOut VARCHAR2
  (
    200 CHAR
  )
  ;
  myPRECISION NUMBER
  (
    38,0
  )
  ;
  myscale NUMBER
  (
    38,0
  )
  ;
BEGIN
  StringOut:=basename;
  myPRECISION:=
  
  CASE
  
  WHEN ( ( p = 0 ) OR ( p IS NULL ) ) THEN
    SS2K5AllPlatform.getPrecision
    (
      basename,m,s
    )
  
  ELSE
    SS2K5AllPlatform.getPrecision
    (
      basename,p,s
    )
  
  END;
  myscale:=SS2K5AllPlatform.getnewscale
  (
    basename,
    
    CASE
    
    WHEN ( (p = 0 ) OR ( p IS NULL ) ) THEN
      m
    
    ELSE
      p
    
    END,s
  )
  ;
  
  IF
    (
      myscale IS NOT NULL
    )
    THEN
    StringOut:=StringOut||'('||myprecision||','||myscale||')';
  
  ELSE
    
    IF
      (
        (myPRECISION IS NOT NULL) AND (myPRECISION != -1 )
      )
      THEN --decision display max_len as a known value
      StringOut:=StringOut||'('||myprecision||')';
    
    END IF;
  
  END IF;
  
  RETURN StringOut;

END;

